import React, { useState, useEffect } from 'react';

// SVG Icons as inline components
const BriefcaseIcon = ({ size = 32, className = '' }) => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width={size} 
    height={size} 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    className={`text-blue-400 ${className}`}
  >
    <rect x="2" y="7" width="20" height="14" rx="2" ry="2" />
    <path d="M16 21V5a2 2 0 0 0-2-2h-4a2 2 0 0 0-2 2v16" />
  </svg>
);

const GitHubIcon = ({ size = 24, className = '' }) => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width={size} 
    height={size} 
    viewBox="0 0 24 24" 
    fill="currentColor" 
    className={`text-gray-300 hover:text-white transition-colors ${className}`}
  >
    <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
  </svg>
);

const LinkIcon = ({ size = 24, className = '' }) => (
  <svg 
    xmlns="http://www.w3.org/2000/svg" 
    width={size} 
    height={size} 
    viewBox="0 0 24 24" 
    fill="none" 
    stroke="currentColor" 
    strokeWidth="2" 
    className={`text-gray-300 hover:text-white transition-colors ${className}`}
  >
    <path d="M10 13a5 5 0 0 0 7.54.54l3-3a5 5 0 0 0-7.07-7.07l-1.72 1.71" />
    <path d="M14 11a5 5 0 0 0-7.54-.54l-3 3a5 5 0 0 0 7.07 7.07l1.71-1.71" />
  </svg>
);

const ProjectCard = ({ project, index }) => {
  const [rotation, setRotation] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    const card = e.currentTarget;
    const rect = card.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    const rotateX = (e.clientY - centerY) / 20;
    const rotateY = -(e.clientX - centerX) / 20;

    setRotation({ x: rotateX, y: rotateY });
  };

  const handleMouseLeave = () => {
    setRotation({ x: 0, y: 0 });
  };

  return (
    <div 
      className="perspective-1000 transform transition-all duration-300 hover:scale-105"
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <div 
        className="bg-gray-800 rounded-xl p-6 shadow-2xl transform transition-transform duration-300 hover:shadow-2xl"
        style={{
          transform: `rotateX(${rotation.x}deg) rotateY(${rotation.y}deg)`,
          transformStyle: 'preserve-3d'
        }}
      >
        <div 
          className="absolute inset-0 bg-gradient-to-br from-transparent to-blue-900/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-xl"
          style={{ transform: 'translateZ(-1px)' }}
        />
        <div className="relative z-10">
          <div className="flex justify-between items-center mb-4">
            <BriefcaseIcon />
            <div className="flex space-x-2">
              <a 
                href={project.githubLink} 
                className="text-gray-300 hover:text-white transition-colors"
              >
                <GitHubIcon />
              </a>
              <a 
                href={project.liveLink} 
                className="text-gray-300 hover:text-white transition-colors"
              >
                <LinkIcon />
              </a>
            </div>
          </div>
          <h3 className="text-2xl font-bold text-white mb-2">{project.name}</h3>
          <p className="text-gray-400 mb-4">{project.description}</p>
          <div className="flex flex-wrap gap-2">
            {project.technologies.map((tech, index) => (
              <span 
                key={index} 
                className="bg-gray-700 text-gray-300 px-2 py-1 rounded-full text-xs"
              >
                {tech}
              </span>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const PersonalWebsite = () => {
  const [activeSection, setActiveSection] = useState('projects');

  const projects = [
    {
      name: 'AI-Powered Dashboard',
      description: 'Advanced analytics platform with machine learning integration',
      technologies: ['React', 'TensorFlow', 'Node.js', 'Docker'],
      githubLink: '#',
      liveLink: '#'
    },
    {
      name: 'Blockchain Voting System',
      description: 'Secure, transparent voting platform using blockchain technology',
      technologies: ['Solidity', 'React', 'Ethereum', 'Web3.js'],
      githubLink: '#',
      liveLink: '#'
    },
    {
      name: 'Real-time Collaboration Tool',
      description: 'Collaborative workspace with instant messaging and file sharing',
      technologies: ['Next.js', 'WebSockets', 'Firebase', 'TypeScript'],
      githubLink: '#',
      liveLink: '#'
    }
  ];

  const renderProjectsSection = () => {
    return (
      <div className="min-h-screen bg-black text-white p-12">
        <div className="container mx-auto">
          <h2 className="text-4xl font-bold mb-12 text-center">
            Featured Projects
          </h2>
          <div className="grid md:grid-cols-3 gap-8">
            {projects.map((project, index) => (
              <ProjectCard 
                key={index} 
                project={project} 
                index={index} 
              />
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-black">
      {renderProjectsSection()}
    </div>
  );
};

export default PersonalWebsite;
