import React, { useState, useEffect } from 'react';

const ProfessionalSpaceBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    // Generate stars
    const newStars = Array.from({ length: 100 }, (_, index) => ({
      id: index,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 2 + 0.5,
      opacity: Math.random() * 0.7 + 0.3,
      speed: Math.random() * 0.1 + 0.05
    }));
    setStars(newStars);
  }, []);

  return (
    <div 
      className="fixed inset-0 pointer-events-none z-0 overflow-hidden"
      style={{ 
        background: 'linear-gradient(to bottom, rgba(0,0,0,0.9) 0%, rgba(10,10,26,0.9) 100%)'
      }}
    >
      {/* Starry background */}
      <div className="absolute inset-0">
        {stars.map(star => (
          <div
            key={star.id}
            className="absolute bg-white/70 rounded-full"
            style={{
              left: `${star.x}%`,
              top: `${star.y}%`,
              width: `${star.size}px`,
              height: `${star.size}px`,
              opacity: star.opacity,
              animation: `twinkle 2s infinite alternate`,
              animationDelay: `${Math.random() * 2}s`
            }}
          />
        ))}
      </div>

      {/* Moon */}
      <div 
        className="absolute top-[10%] right-[10%] bg-gray-300 rounded-full shadow-2xl"
        style={{
          width: '200px',
          height: '200px',
          filter: 'blur(2px)',
          animation: 'float 6s ease-in-out infinite',
          boxShadow: '0 0 50px rgba(200,200,220,0.5)'
        }}
      />

      {/* Planets */}
      <div 
        className="absolute bottom-[20%] left-[15%] bg-purple-700 rounded-full"
        style={{
          width: '100px',
          height: '100px',
          animation: 'orbit 12s linear infinite',
          boxShadow: '0 0 30px rgba(128,0,128,0.5)'
        }}
      />
      <div 
        className="absolute top-[30%] left-[5%] bg-teal-500 rounded-full"
        style={{
          width: '80px',
          height: '80px',
          animation: 'orbit 15s linear infinite reverse',
          boxShadow: '0 0 25px rgba(0,128,128,0.5)'
        }}
      />

      {/* Styles for animations */}
      <style jsx>{`
        @keyframes twinkle {
          0% { opacity: 0.3; }
          100% { opacity: 1; }
        }
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-20px); }
        }
        @keyframes orbit {
          0% { transform: rotate(0deg) translateX(150px) rotate(0deg); }
          100% { transform: rotate(360deg) translateX(150px) rotate(-360deg); }
        }
      `}</style>
    </div>
  );
};

export default ProfessionalSpaceBackground;
